let test_string = ""

let charray = Day5.explode test_string

let get_letter ht letter = if Hashtbl.mem ht letter then Hashtbl.find ht letter else 0

let set_letter ht letter number = if Hashtbl.mem ht letter
  then Hashtbl.remove ht letter;
  Hashtbl.add ht letter number

let required_distance_packet = 4

let required_distance_message = 14

let print_ht ht = print_string "\n";
Hashtbl.iter (fun x y -> Printf.printf "%s -> %i\n" x y) ht

let rec reach_required_distance ht counter duplicates lst rd =
  if counter = rd then
    (ht, lst, duplicates) else
    match lst with
      | h::t -> let h_count = get_letter ht h in
        set_letter ht h (h_count + 1);
        reach_required_distance ht (counter + 1) (duplicates + (if h_count + 1 = 2 then 1 else 0)) t rd
      | _ -> (ht, [], duplicates)

let rec find_marker ht counter duplicates early_lst late_lst =
  if duplicates = 0 then counter else
    match (early_lst, late_lst) with
      | (he::te, hl::tl) ->
        if he = hl then find_marker ht (counter + 1) duplicates te tl else
        let e_count = get_letter ht he in
        let l_count = get_letter ht hl in
        set_letter ht he (e_count - 1);
        set_letter ht hl (l_count + 1);
        find_marker ht (counter + 1) (duplicates + (if l_count = 1 then 1 else 0) - (if e_count = 2 then 1 else 0)) te tl
      | (he::te, _) ->
        let e_count = get_letter ht he in
        set_letter ht he (e_count - 1);
        find_marker ht (counter + 1) (duplicates - (if e_count = 2 then 1 else 0)) te []
      | _ -> -1

let string_list_and_required_distance_to_start_packet sl rd = reach_required_distance (Hashtbl.create 100) 0 0 sl rd

let string_list_and_required_distance_and_start_tuple_to_marker string_list rd start_tuple = match start_tuple with
  | (ht, late_lst, duplicates) -> find_marker ht rd duplicates string_list late_lst

let string_list_and_required_distance_to_marker sl rd = string_list_and_required_distance_and_start_tuple_to_marker sl rd (string_list_and_required_distance_to_start_packet sl rd)

let packet_marker = string_list_and_required_distance_to_marker charray required_distance_packet

let message_marker = string_list_and_required_distance_to_marker charray required_distance_message

let run () =
  print_string "\nStart of packet marker index:\n";
  print_int packet_marker;
  print_string "\nStart of message marker index:\n";
  print_int message_marker;
  print_string "\n";;